INSERT INTO `wp_posts` VALUES (19, 1, '2026-02-26 10:49:20', '2026-02-26 02:49:20', '<div class=\"e_box d_ProSummary p_ProSummary\" data-ename=\"概要描述区\"><article class=\"e_article d_summary p_summary\" data-ename=\"概要描述\">这种0.97英寸的小型电子纸，由于体积紧凑且功耗低，能够满足空间和能源效率至关重要的应用需求。其独特的快速刷新和部分刷新功能确保显示更快更清晰。</article></div>\r\n<div class=\"e_box d_NumBox p_NumBox js_showCode\" data-ename=\"编号区\">\r\n<div class=\"e_title d_num p_num js_code\" data-ename=\"编号\">\r\n<div class=\"font\">100097t6m</div>\r\n</div>\r\n</div>', '0.97英寸小型电子墨水屏迷你电子纸显示屏，GDEM0097T61', '<strong>电子纸显示的基础功能</strong>\r\n<ul>\r\n 	<li>刷新方法：完全刷新2秒，部分刷新0.3秒，4灰度刷新2秒（测试时环境温度25°C）</li>\r\n 	<li>显示方向：水平或垂直</li>\r\n</ul>\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n<strong>ePaper Display 的基本参数</strong>\r\n<ul>\r\n 	<li>产品尺寸：0.97英寸</li>\r\n 	<li>分辨率：184x88</li>\r\n 	<li>PPI：210.27</li>\r\n 	<li>颜色：黑白</li>\r\n 	<li>驱动IC：SSD1680</li>\r\n 	<li>接口：SPI<strong>（18针）</strong></li>\r\n 	<li>工作电压：3.3V</li>\r\n 	<li>工作温度：0-50°C</li>\r\n 	<li>可选定制： <a href=\"https://www.good-display.com/product/146/\" target=\"_blank\" rel=\"noopener\"><u>NFC驱动板</u></a></li>\r\n</ul>\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n<strong>ePaper Display 的主要功能</strong>\r\n<ul>\r\n 	<li>显示功能：双稳态显示（停电后仍保留当前内容）</li>\r\n 	<li>显示模式：纯反射</li>\r\n 	<li>视角：广角超过170度</li>\r\n 	<li>表面处理：硬涂层以防眩光效果</li>\r\n 	<li>节能：不刷新屏幕时无功耗</li>\r\n 	<li>护目：无蓝光发射，零辐射</li>\r\n</ul>', 'publish', 'open', 'closed', '', '0-97%e8%8b%b1%e5%af%b8%e5%b0%8f%e5%9e%8b%e7%94%b5%e5%ad%90%e5%a2%a8%e6%b0%b4%e5%b1%8f%e8%bf%b7%e4%bd%a0%e7%94%b5%e5%ad%90%e7%ba%b8%e6%98%be%e7%a4%ba%e5%b1%8f%ef%bc%8cgdem0097t61', '', '', '2026-02-26 10:56:00', '2026-02-26 02:56:00', '', 0, 'http://localhost/?post_type=product&#038;p=19', 0, 'product', '', 0);
