INSERT INTO `wp_wc_admin_notes` VALUES (1, 'wc-admin-email-improvements', 'info', 'en_US', '商店电子邮件已升级！', '我们对我们的电子邮件模板进行了一些令人兴奋的改进，包括方便购物者浏览的现代化设计和新的自定义选项。 如果您使用的是区块主题，则可以自动同步主题样式！ 请前往电子邮件设置以探索新功能。', '{}', 'unactioned', 'woocommerce-admin', '2026-02-26 01:49:14', NULL, 0, 'plain', '', 0, 1, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (2, 'wc-admin-scheduled-updates-promotion', 'info', 'en_US', 'Analytics 现已支持计划更新', '这可提升您商店的性能表现，请在“分析 &gt; 设置”中将其启用。', '{}', 'unactioned', 'woocommerce-admin', '2026-02-26 01:49:15', NULL, 0, 'plain', '', 0, 1, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (3, 'wc-refund-returns-page', 'info', 'en_US', '设置“退款和退货策略”页面，提升您商店的信誉度。', '我们为您创建了一个退款和货策略页面的样本草稿。 请查看，并根据您的商店进行更新。', '{}', 'unactioned', 'woocommerce-core', '2026-02-26 01:49:15', NULL, 0, 'plain', '', 0, 1, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (4, 'your-first-product', 'info', 'en_US', 'Your first product', 'That’s huge! You’re well on your way to building a successful online store — now it’s time to think about how you’ll fulfill your orders.<br /><br />Read our shipping guide to learn best practices and options for putting together your shipping strategy. And for WooCommerce stores in the United States, you can print discounted shipping labels via USPS with <a href=\"https://href.li/?https://woocommerce.com/shipping\" target=\"_blank\">WooCommerce Shipping</a>.', '{}', 'pending', 'woocommerce.com', '2026-02-26 01:49:17', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (5, 'wc-admin-optimizing-the-checkout-flow', 'info', 'en_US', 'Optimizing the checkout flow', 'It’s crucial to get your store’s checkout as smooth as possible to avoid losing sales. Let’s take a look at how you can optimize the checkout experience for your shoppers.', '{}', 'pending', 'woocommerce.com', '2026-02-26 01:49:17', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (6, 'wc-payments-qualitative-feedback', 'info', 'en_US', 'WooPayments setup - let us know what you think', 'Congrats on enabling WooPayments for your store. Please share your feedback in this 2 minute survey to help us improve the setup process.', '{}', 'pending', 'woocommerce.com', '2026-02-26 01:49:17', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (7, 'share-your-feedback-on-paypal', 'info', 'en_US', 'Share your feedback on PayPal', 'Share your feedback in this 2 minute survey about how we can make the process of accepting payments more useful for your store.', '{}', 'pending', 'woocommerce.com', '2026-02-26 01:49:17', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (8, 'google_listings_and_ads_install', 'marketing', 'en_US', 'Drive traffic and sales with Google', 'Reach online shoppers to drive traffic and sales for your store by showcasing products across Google, for free or with ads.', '{}', 'pending', 'woocommerce.com', '2026-02-26 01:49:17', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (9, 'wc-subscriptions-security-update-3-0-15', 'info', 'en_US', 'WooCommerce Subscriptions security update!', 'We recently released an important security update to WooCommerce Subscriptions. To ensure your site’s data is protected, please upgrade <strong>WooCommerce Subscriptions to version 3.0.15</strong> or later.<br /><br />Click the button below to view and update to the latest Subscriptions version, or log in to <a href=\"https://woocommerce.com/my-dashboard\">WooCommerce.com Dashboard</a> and navigate to your <strong>Downloads</strong> page.<br /><br />We recommend always using the latest version of WooCommerce Subscriptions, and other software running on your site, to ensure maximum security.<br /><br />If you have any questions we are here to help — just <a href=\"https://woocommerce.com/my-account/create-a-ticket/\">open a ticket</a>.', '{}', 'pending', 'woocommerce.com', '2026-02-26 01:49:17', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_wc_admin_notes` VALUES (10, 'woocommerce-core-update-5-4-0', 'info', 'en_US', 'Update to WooCommerce 5.4.1 now', 'WooCommerce 5.4.1 addresses a checkout issue discovered in WooCommerce 5.4. We recommend upgrading to WooCommerce 5.4.1 as soon as possible.', '{}', 'pending', 'woocommerce.com', '2026-02-26 01:49:17', NULL, 0, 'plain', '', 0, 0, 'info');
